    private bool AddingNewBook = false;
    private BookService _bookService;

    private List<Book> Books = new List<Book>();
    private Book BookToAdd;


    private string searchCriteria = string.Empty;

    protected override async Task OnInitializedAsync()
    {
        _bookService = new BookService();
        GetBooks();
    }

    private void GetBooks()
    {
        Books = _bookService.GetAllBooks();
    }

    private void NotAdding()
    {
        AddingNewBook = false;
    }

    private void Adding()
    {
        BookToAdd = new Book();
        AddingNewBook = true;
    }

    private void EditBook(Book book)
    {
        if (book != null)
        {
            BookToAdd = book;
        }
        AddingNewBook = true;
    }


    private void SaveBook()
    {
        if (AddingNewBook == true)
        {
            _bookService.SaveBook(BookToAdd);
        }

        GetBooks();
        NotAdding();
    }

    private async Task DeleteBook(Book book)
    {
        if (!await JSRuntime.InvokeAsync<bool>("confirm", $"Are you sure you want to delete '{book.Title}'?"))
            return;

        _bookService.DeleteBook(book.BookId);
        GetBooks();
    }

    private void Search()
    {
        Books = _bookService.SearchBooks(searchCriteria);
    }

    private void Clear()
    {
        searchCriteria = string.Empty;
        GetBooks();
    }

